import axios from 'axios';

const API_URL = 'http://localhost:5000/api/calculators'; // Адрес твоего сервера

// Получить все калькуляторы
export const getCalculators = async () => {
  try {
    const response = await axios.get(API_URL);
    return response.data;
  } catch (error) {
    console.error("Ошибка при получении калькуляторов:", error);
    throw error;
  }
};

// Получить один калькулятор по типу
export const getCalculator = async (type) => {
  try {
    const response = await axios.get(`${API_URL}/type/${type}`);
    return response.data;
  } catch (error) {
    console.error("Ошибка при получении калькулятора:", error);
    throw error;
  }
};


// Добавить новый калькулятор
export const createCalculator = async (calculator) => {
  try {
    const response = await axios.post(API_URL, calculator);
    return response.data;
  } catch (error) {
    console.error("Ошибка при создании калькулятора:", error);
    throw error;
  }
};
