import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { getCalculator } from '../api'; // Будем использовать новый метод для получения калькулятора по ID

function CalculatorPage() {
  const { type } = useParams();
  const [calculator, setCalculator] = useState(null);

  useEffect(() => {
    const fetchCalculator = async () => {
      try {
        const data = await getCalculator(type); // Запрос по типу калькулятора
        setCalculator(data);
      } catch (error) {
        console.error("Ошибка при загрузке калькулятора:", error);
      }
    };

    fetchCalculator();
  }, [type]);

  return (
    <div>
      {calculator ? (
        <>
          <h2>Калькулятор: {calculator.name}</h2>
          <p>{calculator.description}</p>
          <p>Ставка по умолчанию: {calculator.defaultRate}%</p>
          <h3>Поля:</h3>
          <ul>
            {calculator.fields.map((field, index) => (
              <li key={index}>{field}</li>
            ))}
          </ul>
        </>
      ) : (
        <p>Загружается калькулятор...</p>
      )}
    </div>
  );
}

export default CalculatorPage;
