import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function Home() {
  const [calculators, setCalculators] = useState([]);

  useEffect(() => {
    const fetchCalculators = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/calculators');
        setCalculators(response.data);
      } catch (err) {
        console.error('Ошибка загрузки калькуляторов', err);
      }
    };
    fetchCalculators();
  }, []);

  return (
    <div>
      <h2>Главная страница</h2>
      <p>Список доступных калькуляторов:</p>
      <ul>
        {calculators.map((calculator) => (
          <li key={calculator._id}>
            <Link to={`/calculator/${calculator.type}`}>
              {calculator.name} - {calculator.type}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
