import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function CalculatorPage() {
  const { type } = useParams();
  const [calculator, setCalculator] = useState(null);
  const [values, setValues] = useState({});

  useEffect(() => {
    const fetchCalculator = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/calculators/${type}`);
        setCalculator(response.data);
        const initialValues = {};
        response.data.fields.forEach((field) => {
          initialValues[field] = '';
        });
        setValues(initialValues);
      } catch (err) {
        console.error('Ошибка загрузки калькулятора', err);
      }
    };
    fetchCalculator();
  }, [type]);

  const handleChange = (e) => {
    setValues({
      ...values,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Логика расчета или отправки данных на сервер
  };

  return (
    <div>
      {calculator ? (
        <div>
          <h2>Калькулятор: {calculator.name}</h2>
          <form onSubmit={handleSubmit}>
            {calculator.fields.map((field) => (
              <div key={field}>
                <label>{calculator.template[field]}</label>
                <input
                  type="text"
                  name={field}
                  value={values[field]}
                  onChange={handleChange}
                />
              </div>
            ))}
            <button type="submit">Рассчитать</button>
          </form>
        </div>
      ) : (
        <p>Загружается...</p>
      )}
    </div>
  );
}

export default CalculatorPage;
