import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Home() {
  const [calculators, setCalculators] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Получение списка калькуляторов с сервера
  useEffect(() => {
    axios
      .get('http://localhost:5000/api/calculators') // URL для получения калькуляторов
      .then((response) => {
        setCalculators(response.data);
        setLoading(false);
      })
      .catch((err) => {
        setError('Не удалось загрузить калькуляторы');
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Главная страница</h2>
      <p>Список доступных калькуляторов:</p>
      <ul>
        {calculators.map((calculator) => (
          <li key={calculator._id}>
            <Link to={`/calculator/${calculator.type}`}>{calculator.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
