import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Admin() {
  const [name, setName] = useState('');
  const [type, setType] = useState('');
  const [defaultRate, setDefaultRate] = useState('');
  const [fields, setFields] = useState(['']); // Массив для полей
  const [template, setTemplate] = useState({});
  const [types, setTypes] = useState(['mortgage', 'car', 'consumer']); // Типы калькуляторов
  const [fieldType, setFieldType] = useState('text');
  const [calculators, setCalculators] = useState([]);
  const [error, setError] = useState(null);

  // Загружаем все калькуляторы при монтировании компонента
  useEffect(() => {
    const fetchCalculators = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/calculators');
        setCalculators(response.data);
      } catch (err) {
        setError('Не удалось загрузить калькуляторы');
      }
    };
    fetchCalculators();
  }, []);

  // Функция для добавления поля
  const addField = () => {
    setFields([...fields, '']);
  };

  const handleFieldChange = (e, index) => {
    const newFields = [...fields];
    newFields[index] = e.target.value;
    setFields(newFields);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/calculators', {
        name,
        type,
        defaultRate,
        fields,
        template,
      });
      setCalculators([...calculators, response.data]);
      setError(null);
    } catch (err) {
      setError('Не удалось создать калькулятор');
    }
  };

  // Получить шаблон калькулятора при выборе типа
  const handleTypeChange = (e) => {
    setType(e.target.value);
    if (e.target.value === 'mortgage') {
      setTemplate({
        amount: 'Сумма кредита',
        rate: 'Годовая процентная ставка',
        term: 'Срок кредита (лет)',
      });
    } else if (e.target.value === 'car') {
      setTemplate({
        amount: 'Сумма кредита',
        rate: 'Годовая процентная ставка',
        term: 'Срок кредита (лет)',
      });
    } else {
      setTemplate({});
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/calculators/${id}`);
      setCalculators(calculators.filter((calc) => calc._id !== id));
    } catch (err) {
      setError('Не удалось удалить калькулятор');
    }
  };

  const handleEdit = (calculator) => {
    setName(calculator.name);
    setType(calculator.type);
    setDefaultRate(calculator.defaultRate);
    setFields(calculator.fields);
    setTemplate(calculator.template);
  };

  return (
    <div>
      <h2>Добавить новый калькулятор</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Название калькулятора:</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Тип калькулятора:</label>
          <select value={type} onChange={handleTypeChange}>
            <option value="">Выберите тип калькулятора</option>
            {types.map((t) => (
              <option key={t} value={t}>
                {t.charAt(0).toUpperCase() + t.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div>
          <label>Ставка по умолчанию:</label>
          <input
            type="number"
            value={defaultRate}
            onChange={(e) => setDefaultRate(e.target.value)}
            required
          />
        </div>

        <div>
          <label>Тип поля:</label>
          <select value={fieldType} onChange={(e) => setFieldType(e.target.value)}>
            <option value="text">Текст</option>
            <option value="number">Число</option>
            <option value="date">Дата</option>
          </select>
        </div>

        <div>
          <button type="button" onClick={addField}>
            Добавить поле
          </button>
        </div>

        {fields.map((field, index) => (
          <div key={index}>
            <input
              type={fieldType}
              value={field}
              onChange={(e) => handleFieldChange(e, index)}
              required
            />
          </div>
        ))}

        <button type="submit">Сохранить калькулятор</button>
      </form>

      {error && <p>{error}</p>}

      <h2>Список калькуляторов</h2>
      <ul>
        {calculators.map((calculator) => (
          <li key={calculator._id}>
            <span>{calculator.name} ({calculator.type})</span>
            <button onClick={() => handleEdit(calculator)}>Редактировать</button>
            <button onClick={() => handleDelete(calculator._id)}>Удалить</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Admin;
